import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import {
  Box,
  Container,
  Grid,
  Typography,
  Button,
  Card,
  CardContent,
  CardMedia,
  Chip,
  Rating,
  Divider,
  Tabs,
  Tab,
  IconButton,
  Stack,
  Paper,
  useTheme,
  useMediaQuery,
} from '@mui/material';
import {
  LocationOn,
  LocalShipping,
  Phone,
  Email,
  ArrowBack,
  CheckCircle,
} from '@mui/icons-material';

// Mock data for the product
const mockProduct = {
  id: '1',
  name: 'Rice Husk',
  description: 'High-quality rice husk from Punjab farms. Perfect for biomass energy production and mushroom cultivation.',
  price: 1500,
  quantity: '10000 kg',
  location: 'Ludhiana, Punjab',
  seller: 'Punjab Agro Farms',
  image: 'https://images.unsplash.com/photo-1605000797499-95a51c5269ae?ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=60',
  category: 'Crop Residue',
  contact: '+91 98765 43210',
  email: 'punjabagro@example.com',
  rating: 4.5,
  reviewCount: 28,
  isVerified: true,
  details: {
    moistureContent: '12%',
    ashContent: '15%',
    calorificValue: '3000 kcal/kg',
    packaging: '50 kg bags',
    minimumOrder: '1000 kg',
    deliveryTime: '3-5 business days',
  },
  uses: [
    'Biomass energy production',
    'Mushroom cultivation',
    'Animal bedding',
    'Composting',
    'Building materials',
  ],
  reviews: [
    {
      id: 1,
      user: 'BioEnergy Solutions',
      rating: 5,
      comment: 'Excellent quality rice husk. Perfect for our biomass plant.',
      date: '2024-02-15',
    },
    {
      id: 2,
      user: 'Mushroom Farms India',
      rating: 4,
      comment: 'Good product for mushroom cultivation. Would recommend.',
      date: '2024-02-10',
    },
    {
      id: 3,
      user: 'Green Compost Co.',
      rating: 4.5,
      comment: 'Consistent quality and reliable delivery.',
      date: '2024-02-05',
    },
  ],
};

const ProductDetailPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));
  const [tabValue, setTabValue] = useState(0);

  const handleTabChange = (event, newValue) => {
    setTabValue(newValue);
  };

  const handleBack = () => {
    navigate(-1);
  };

  const handleContactSeller = () => {
    // In a real application, this would open a chat or contact form
    window.location.href = `mailto:${mockProduct.email}`;
  };

  return (
    <Container maxWidth="lg" sx={{ py: 4 }}>
      <Box sx={{ mb: 4 }}>
        <Button
          startIcon={<ArrowBack />}
          onClick={handleBack}
          sx={{ mb: 2 }}
        >
          Back to Marketplace
        </Button>
      </Box>

      <Grid container spacing={4}>
        {/* Product Image */}
        <Grid item xs={12} md={6}>
          <Card>
            <CardMedia
              component="img"
              height="400"
              image={mockProduct.image}
              alt={mockProduct.name}
              sx={{ objectFit: 'cover' }}
            />
          </Card>
        </Grid>

        {/* Product Info */}
        <Grid item xs={12} md={6}>
          <Box sx={{ mb: 3 }}>
            <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
              <Typography variant="h4" component="h1" sx={{ fontWeight: 700 }}>
                {mockProduct.name}
              </Typography>
              {mockProduct.isVerified && (
                <CheckCircle
                  color="success"
                  sx={{ ml: 1 }}
                />
              )}
            </Box>
            <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
              <Rating value={mockProduct.rating} precision={0.5} readOnly />
              <Typography variant="body2" color="text.secondary" sx={{ ml: 1 }}>
                ({mockProduct.reviewCount} reviews)
              </Typography>
            </Box>
            <Typography variant="h5" color="primary" sx={{ fontWeight: 700, mb: 2 }}>
              ₹{mockProduct.price.toLocaleString()}
            </Typography>
            <Typography variant="body1" sx={{ mb: 3 }}>
              {mockProduct.description}
            </Typography>
            <Box sx={{ mb: 3 }}>
              <Chip
                label={mockProduct.category}
                color="primary"
                variant="outlined"
                sx={{ mr: 1 }}
              />
              <Chip
                label={mockProduct.quantity}
                color="secondary"
                variant="outlined"
              />
            </Box>
            <Box sx={{ mb: 3 }}>
              <Stack direction="row" spacing={2} alignItems="center">
                <LocationOn color="action" />
                <Typography>{mockProduct.location}</Typography>
              </Stack>
              <Stack direction="row" spacing={2} alignItems="center">
                <LocalShipping color="action" />
                <Typography>Delivery available</Typography>
              </Stack>
            </Box>
            <Button
              variant="contained"
              size="large"
              fullWidth
              onClick={handleContactSeller}
              sx={{ mb: 2 }}
            >
              Contact Seller
            </Button>
            <Button
              variant="outlined"
              size="large"
              fullWidth
              startIcon={<Phone />}
              href={`tel:${mockProduct.contact}`}
            >
              Call Seller
            </Button>
          </Box>
        </Grid>

        {/* Product Details Tabs */}
        <Grid item xs={12}>
          <Paper sx={{ width: '100%' }}>
            <Tabs
              value={tabValue}
              onChange={handleTabChange}
              indicatorColor="primary"
              textColor="primary"
              variant="fullWidth"
            >
              <Tab label="Details" />
              <Tab label="Uses" />
              <Tab label="Reviews" />
            </Tabs>
          </Paper>

          <Box sx={{ mt: 3 }}>
            {tabValue === 0 && (
              <Grid container spacing={2}>
                {Object.entries(mockProduct.details).map(([key, value]) => (
                  <Grid item xs={12} sm={6} key={key}>
                    <Paper sx={{ p: 2 }}>
                      <Typography variant="subtitle2" color="text.secondary">
                        {key.replace(/([A-Z])/g, ' $1').trim()}
                      </Typography>
                      <Typography variant="body1">{value}</Typography>
                    </Paper>
                  </Grid>
                ))}
              </Grid>
            )}

            {tabValue === 1 && (
              <Grid container spacing={2}>
                {mockProduct.uses.map((use, index) => (
                  <Grid item xs={12} sm={6} md={4} key={index}>
                    <Paper sx={{ p: 2 }}>
                      <Typography variant="body1">{use}</Typography>
                    </Paper>
                  </Grid>
                ))}
              </Grid>
            )}

            {tabValue === 2 && (
              <Box>
                {mockProduct.reviews.map((review) => (
                  <Box key={review.id} sx={{ mb: 3 }}>
                    <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
                      <Typography variant="subtitle1" sx={{ fontWeight: 600 }}>
                        {review.user}
                      </Typography>
                      <Typography variant="body2" color="text.secondary">
                        {review.date}
                      </Typography>
                    </Box>
                    <Rating value={review.rating} readOnly size="small" />
                    <Typography variant="body1" sx={{ mt: 1 }}>
                      {review.comment}
                    </Typography>
                    <Divider sx={{ mt: 2 }} />
                  </Box>
                ))}
              </Box>
            )}
          </Box>
        </Grid>
      </Grid>
    </Container>
  );
};

export default ProductDetailPage;
